    var canvas = $('#canvas')
    var val = canvas.attr('data-val')
    var _val = 0
    var degrees = 0

    var t = setInterval(function () {
      degrees = 0.75 + (1.5 * _val) / 100
      draw(degrees)
      if (_val == val) {
        clearInterval(t)
      } else {
        _val++
        if (_val > val) {
